                  <label>Max 15:</label>
                  <br></br>
                  <label>Current price: {ashPrice} $ASH per rug</label>
                  <br></br>
                  <input
                  value= {amountAsh}
                  onChange= {event => setAmountAsh(event.target.value)}
                  />
                </div>
                </form>
                <p>{messageAsh}</p>
                <button onClick={mintAsh}>Mint</button>
                </div>


        </div>
    );

